<?php    $page = new stdClass();    $page->title = 'Travelling Salesman Problem';    include('includes/header.php');?><script src="Node/lib/simplega.js" language="javascript"></script><script src="Node/lib/tsp.js" language="javascript"></script><div class="row"><input type="button" class="btn btn-primary" value="Relaunch" onclick="relaunch()"></div><div class="row"><canvas id="canvas" width="600" height="500"></canvas><canvas id="canvas2" width="100" height="100"></canvas></div><script language="javascript">var canvas = document.getElementById("canvas");var context = canvas.getContext("2d");var canvas2 = document.getElementById("canvas2");var context2 = canvas2.getContext("3d");function drawPoints(){    context.clearRect(0, 0, canvas.width, canvas.height);    context.beginPath();    for (var k = 0; k < 5; k++)        for (var j = 0; j < 4; j++) {            context.arc(50 + 100 * k, 50 + 100 * j, 10, 0, Math.PI * 2, true);        }    context.closePath();    context.fill();}function drawValues(points, values){    context.beginPath();        var l = values.length;    var point = points[values[0]];        context.moveTo(50 + 100 * point.x, 50 + 100 * point.y);        for (var k = 1; k < l; k++) {        point = points[values[k]];        context.lineTo(50 + 100 * point.x, 50 + 100 * point.y);    }        context.closePath();    context.stroke();}function drawPopulation(canvas, population, bestvalue) {    var ctx = canvas.getContext("2d");    var width = canvas.width;    var height = canvas.height;        var canvasData = ctx.getImageData(0, 0, width, height);        var l = population.length;        for (var k = 0; k < l; k++) {        var x = k % width;        var y = (k - x) / width;        drawPixel(canvasData, width, x, y, (bestvalue - population[k].evaluate()) * 10 % 255, 0, 0, 255);    }        ctx.putImageData(canvasData, 0, 0);}// http://stackoverflow.com/questions/7812514/drawing-a-dot-on-html5-canvas// That's how you define the value of a pixel //function drawPixel (canvasData, width, x, y, r, g, b, a) {    var index = (x + y * width) * 4;    canvasData.data[index + 0] = r;    canvasData.data[index + 1] = g;    canvasData.data[index + 2] = b;    canvasData.data[index + 3] = a;}    var points = tsp.createPointRectangle(5, 4);var maxlength = 5 * 4 * (5*5 + 4*4);drawPoints();var engine = new simplega.Engine();var population = tsp.createPopulation(10000, points, maxlength);engine.setMutators([new tsp.Mutator()]);var bestvalue = 0;var bestpath;setTimeout(doStep, 10);function doStep() {    engine.setPopulation(population);    population = engine.nextPopulation();    var newbestvalue = simplega.getBestValue(population);    drawPopulation(canvas2, population, newbestvalue);        if (newbestvalue >= bestvalue) {        bestvalue = newbestvalue;        var l = population.length;        for (var k = 0; k < l; k++)            if (population[k].evaluate() == newbestvalue) {                drawPoints();                drawValues(points, population[k].getValues());            }    }        setTimeout(doStep, 10);}function relaunch() {    var newpopulation = tsp.createPopulation(10000, points, maxlength);            bestvalue = 0;    population = newpopulation;}</script><?php    include('includes/footer.php');?>